mapper.select:
select b.board_id, b.title, m.name, b.views, b.reg_date from board b left join member m on b.member_id=m.member_id where category=#{category}
AND b.title LIKE CONCAT('%', #{searchKeyword}, '%')
AND b.content LIKE CONCAT('%', #{searchKeyword}, '%')
AND m.name LIKE CONCAT('%', #{searchKeyword}, '%')
order by b.board_id desc limit #{size} offset #{offset}
