mapper.select:
select b.board_id, b.title, m.name, b.views, b.reg_date from board b left join member m on b.member_id=m.member_id where b.category=#{category}
AND b.title LIKE CONCAT('%', #{searchKeyword}, '%')
AND b.content LIKE CONCAT('%', #{searchKeyword}, '%')
AND m.name LIKE CONCAT('%', #{searchKeyword}, '%')
order by b.board_id desc limit #{size} offset #{offset}

mapper.update:
UPDATE board SET
views = views + 1
where category = #{category} AND board_id = #{board_id}

mapper.select:
select b.board_id, b.title, m.name, b.views, b.reg_date, b.category
from board b
left join member m on b.member_id = m.member_id
where b.member_id = #{member_id}

    AND b.category = #{type}


    AND (
    b.title LIKE CONCAT('%', #{keyword}, '%')
    OR b.content LIKE CONCAT('%', #{keyword}, '%')
    )



        order by b.board_id asc


        order by b.board_id desc


limit #{size} offset #{offset}

select count(*)
from board b
left join member m on b.member_id = m.member_id
where b.member_id = #{member_id}

    AND b.category = #{type}


    AND (
    b.title LIKE CONCAT('%', #{keyword}, '%')
    OR b.content LIKE CONCAT('%', #{keyword}, '%')
    )
