const { default: styled } = require("styled-components");

const CommentListStyle = styled.div`
  .comment-list {

    li {
      min-height: 93px;

      input {
        width: 100%;
        height: 30px;
        padding: 10px;
        font-size: 16px;
        border: none;
        border-bottom: 2px solid #eee;
        border-radius: 5px;
        box-sizing: border-box;
      }

      .button-row {
        margin-top: 10px;
        display: flex;
        justify-content: flex-end; /* 오른쪽 정렬 */

        button {
          width: 50px;
          height: 25px;
          margin-left: 3px;
          background-color: #f8e776;
          color: #333;
          border: none;
          border-radius: 5px;
          cursor: pointer;
          font-size: 16px;
        }
      }

      .meta-row {
        min-height: 40px;
        display: flex;
        justify-content: space-between; /* 오른쪽 정렬 */
        margin-top: 4px;
        align-items: center;
        border-bottom: 1px solid #eee;
        margin-bottom: 15px;
        
      }

      .regdate {
        font-size: 16px;
        color: #888;
        margin-left: 5px;
      }

      .button-group {
        display: flex;
        margin-bottom: 5px;

        button {
        width: 50px;
        height: 25px;
        margin-left: 3px;
        background-color: #f8e776;
        color: #333;
        border: none;
        border-radius: 5px;
        cursor: pointer;
        font-size: 15px;
        }

      }
      
    }
  }

`;

export default CommentListStyle;
