const { default: styled } = require("styled-components");

const CommentFormStyle = styled.div`
  comment-form {
    display: flex;
    align-items: center;
    gap: 8px;
    background: #f8e776;
    padding: 10px 16px;
    border-radius: 8px;
    box-shadow: 0 2px 8px rgba(248, 231, 118, 0.12);

    input {
      flex: 1;
      border: 1.5px solid #f8e776;
      border-radius: 4px;
      padding: 8px 12px;
      font-size: 16px;
      background: #fffbe6;
      transition: border-color 0.2s, background 0.2s;

      &:focus {
        outline: none;
        border-color: #ffe066;
        background: #fffde3;
      }
    }

    button {
      background: #f8e776;
  color: #333;
  border: none;
  border-radius: 4px;
  padding: 8px 16px;
  font-weight: bold;
  cursor: pointer;
  transition: background 0.2s, box-shadow 0.2s;
  box-shadow: 0 1px 4px rgba(248, 231, 118, 0.15);

  &:hover {
    background: #ffe066;
  }
    }
  }
`;

export default CommentFormStyle;
