const OrderApi = {
  // ~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~ Ï£º  Î¨∏ ~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~
  // <Order />
  order: async () => {
    const result = await instance.post(`/order`);
    return result.data;
  },

  // <OrderDetail /> ÌéòÏù¥Ïßï
  getOrderDetailPageList: async (pages) => {
    alert(`getPageList() Ìò∏Ï∂úÎê®, pages = ${JSON.stringify(pages)}`);
    const result = await instance.post(`/order/detail`, pages);
    console.log('üì¶ ÏùëÎãµ Í≤∞Í≥º:', result);
    alert(`getPageList() Ìò∏Ï∂úÎê®, result = ${result.data}`);
    return result.data;
  },

  // ~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~ Í≤∞  Ï†ú ~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~
  // Í≤∞Ï†ú
  pay: async (payload) => {
    payload.goodsList.forEach((item) => {
      console.log(`Í≤∞Ï†ú ÏãúÎèÑ => ÏÉÅÌíà ID: ${item.goodsId}, ÏÉÅÌíà ÏàòÎüâ: ${item.quantity}`);
    });
    const result = await instance.post(`/order/pay`, payload);
    try {
      if (result != null) {
        return result.data;
      } else {
        alert(`OrderApi.pay() null`);
      }
    } catch (err) {
      console.error('Ïò§Î•ò Î∞úÏÉù:', err);
      alert('OrderApi.pay() ÏàòÌñâÏ§ë ÏóêÎü¨Î∞úÏÉù.');
    }

    return result.data;
    //console.log(JSON.stringify(payload, null, 2));
  },
  findAddress: async () => {
    const result = await instance.get(`/goods/findaddress`);
    try {
      if (result != null) {
        return result.data;
      } else {
        alert(`OrderApi.findAddress() null`);
      }
    } catch (err) {
      console.error('Ïò§Î•ò Î∞úÏÉù:', err);
      alert('OrderApi.findAddress() ÏàòÌñâÏ§ë ÏóêÎü¨Î∞úÏÉù.');
    }
    return result.data;
  },
};

export default OrderApi;
