const { default: styled } = require("styled-components");

const CommentListStyle = styled.div`
  .comment-list {

    li {

      input {
        width: 100%;
        height: 30px;
        padding: 10px;
        font-size: 16px;
        border: none;
        border-bottom: 2px solid #eee;
        border-radius: 5px;
        box-sizing: border-box;
      }

      .button-row {
        display: flex;
        justify-content: flex-end; /* 오른쪽 정렬 */
        gap: 10px;                /* 버튼 사이 간격 */
        margin-top: 8px;
      }

      button {
        width: 5%;
        height: 30px;
        margin-left: 10px;
        background-color: #f8e776;
        color: #333;
        border: none;
        border-radius: 5px;
        cursor: pointer;
        font-size: 16px;
      }
    }
  }

`;

export default CommentListStyle;
