import axios from 'axios';
const KH_DOMAIN = 'http://localhost:8080';
export async function goodsApi(uri, method, prodId) {
  // 1.1) headers
  let headers = '';

  // 1.2) axios 전송 options
  let options = {
    url: `${KH_DOMAIN}/${uri}/${prodId}`,
    method: method,
  };

  if (uri.indexOf('cart') >= 0) {
    headers = { 'Content-Type': 'application/json' };
  } else {
    headers = { 'Content-Type': 'multipart/form-data' };
  }

  // 1.3) 전송 Data(requestData) 있는 경우 data 속성 추가


  // 1.3) Axios 요청
  return await axios(options)
    .then((response) => {
      return response.data;
    })
    .catch((err) => {
      console.error(`** apiCall Error status=${err.response.status}, message=${err.message}`);
      return Promise.reject(err.response.status);
    }); //catch
} //apiCall
