import React, { useEffect, useState } from "react";
import axios from "axios";
import BoardListStyle from "./BoardListStyle";
import { useNavigate, useParams, useSearchParams } from "react-router-dom";
import PageNumber from "../util/PageNumber";


export default function BoardList() {
  const { category } = useParams(); // URL ÌååÎùºÎØ∏ÌÑ∞ÏóêÏÑú Ïπ¥ÌÖåÍ≥†Î¶¨ Ï∂îÏ∂ú
  const [listData, setListData] = useState([]);
  const [error, setError] = useState(null);
  const navigate = useNavigate();
  //const [searchType, setSearchType] = useState("title");
  //const [searchKeyword, setSearchKeyword] = useState("");

  // pageNumber ÏÉÅÌÉú Î≥ÄÏàò Ï∂îÍ∞Ä
  const [paging, setPaging] = useState({
    page: 0,
    size: 3,
    totalElements: 0,
    totalPages: 1,
    isPrev: false,
    isNext: true,
    start: 0,
    end: 1,
  });
  //const [page, setPage] = useState(0);
  const [searchParams, setSearchParams] = useSearchParams();

  const page = Number(searchParams.get("page") || 0);
  const searchType = searchParams.get("searchType") || "title";
  const searchKeyword = searchParams.get("searchKeyword") || "";
  const [isComposing, setIsComposing] = useState(false);

  // Ïπ¥ÌÖåÍ≥†Î¶¨Î≥Ñ API ÏóîÎìúÌè¨Ïù∏Ìä∏ Îß§Ìïë
  const categoryApiMap = {
    notice: "/board/boardList/notice",
    community: "/board/boardList/community",
    faq: "/board/boardList/faq",
    free: "/board/boardList/free"
  };

  // Ïπ¥ÌÖåÍ≥†Î¶¨Î≥Ñ Í≤åÏãúÌåê Ïù¥Î¶Ñ Îß§Ìïë
  const categoryNameMap = {
    notice: "Í≥µÏßÄÏÇ¨Ìï≠",
    community: "Ïª§ÎÆ§ÎãàÌã∞",
    faq: "Î¨∏Ïùò/FAQ",
    free: "ÏûêÏú†Í≤åÏãúÌåê"
  };

  

  useEffect(() => {
    // Ïπ¥ÌÖåÍ≥†Î¶¨Í∞Ä ÏóÜÏúºÎ©¥ Í∏∞Î≥∏ boardÎ°ú ÏÑ§Ï†ï
    const apiUrl = categoryApiMap[category] || "/board/boardList/free";
    const params = { page, size: paging.size };
    if (searchKeyword.trim() !== "") {
      params.searchType = searchType;
      params.searchKeyword = searchKeyword;
    }
    axios
      .get(apiUrl, { params })
      .then((response) => {
        setListData(response.data.content || []);
        let temp = Math.floor(page / 3) * 3;
        setPaging(prev => ({ 
          ...prev,
          page: response.data.page,
          size: response.data.size,
          totalElements: response.data.totalElements,
          totalPages: response.data.totalPages,
          isPrev: response.data.prev,                 // javaBean Í∑úÏïΩÏúºÎ°ú Ïù∏Ìï¥ boolean typeÏùò Î≥ÄÏàòÎäî isPrevÍ∞Ä ÏïÑÎãå prevÎ°ú ÎêòÏñ¥ÏûàÏùå
          isNext: response.data.next,
          start: temp,
          end: Math.min(temp +3, response.data.totalPages),
        }));
      })
      .catch((error) => setError(error));
      // eslint-disable-next-line
  }, [category, page]);

  if (error) {
    // ÏÑúÎ≤Ñ ÏóêÎü¨ ÏΩîÎìúÏóê Îî∞Îùº Î©îÏãúÏßÄ Î∂ÑÍ∏∞
    if (error.response && error.response.status === 502) {
      return <div>{error.response.data}</div>;
    }
    return <div>Í≤åÏãúÌåêÏùÑ Î∂àÎü¨Ïò§ÏßÄ Î™ªÌñàÏäµÎãàÎã§. =&gt; {error.message}</div>;
  }

  // [ÏàòÏ†ï] ÌéòÏù¥ÏßÄ Î≥ÄÍ≤Ω Ìï∏Îì§Îü¨
  const handlePageChange = (newPage) => {
    setSearchParams({
      page: newPage,
      searchType,
      searchKeyword
    });
  };

  //Í≤ÄÏÉâ Í∏∞Îä•
  const handleSearch = (e) => {
    if(e) e.preventDefault(); // Ìèº Ï†úÏ∂ú Ïãú ÏÉàÎ°úÍ≥†Ïπ® Î∞©ÏßÄ
    const apiUrl = categoryApiMap[category] || "/board/boardList/free";
    axios.get(apiUrl, {
      params: {
        page: 0,
        size: paging.size,
        searchType,
        searchKeyword
      }
    })
    .then((response) => {
      setListData(response.data.content || []);
      setPaging({
        page: response.data.page,
        size: response.data.size,
        totalElements: response.data.totalElements,
        totalPages: response.data.totalPages,
        isPrev: response.data.prev,     
        isNext: response.data.next,
        start: 0,
        end: Math.min(3, response.data.totalPages),
      });
    })
    .catch((error) => { setError(error);
      console.error("Í≤ÄÏÉâ Ï§ë Ïò§Î•ò Î∞úÏÉù:", error);
    });
  };

  // [Ï∂îÍ∞Ä] Í≤ÄÏÉâ ÏòµÏÖò/Í≤ÄÏÉâÏñ¥ Î≥ÄÍ≤Ω Ìï∏Îì§Îü¨ (Í≤ÄÏÉâÏùÄ ÏóîÌÑ∞/Î≤ÑÌäºÏóêÏÑúÎßå ÎèôÏûë)
  const handleSearchTypeChange = (e) => {
    setSearchParams({
      page: page,
      searchType: e.target.value,
      searchKeyword
    });
  };
  const handleSearchKeywordChange = (e) => {
    if (isComposing) return;    // Ï°∞Ìï© Ï§ëÏù¥Î©¥ Î¨¥Ïãú
    setSearchParams({
      page: page,
      searchType,
      searchKeyword: e.target.value
    });
  };

  // [Ï∂îÍ∞Ä] IME Ï°∞Ìï© ÏÉÅÌÉú Í¥ÄÎ¶¨
  const handleComposition = (e) => {
    if (e.type === "compositionstart") setIsComposing(true);
    if (e.type === "compositionend") setIsComposing(false);
