import React, { useEffect, useState } from "react";
import { useParams, useNavigate } from "react-router-dom";
import axios from "axios";
import BoardDetailStyle from "./BoardDetailStyle";
import 'react-comments-section/dist/index.css';
import { CommentSection } from "react-comments-section";

// jwt ÌÜ†ÌÅ∞ÏóêÏÑú Î°úÍ∑∏Ïù∏Ìïú ÌöåÏõêÏùò IDÎ•º Í∞ÄÏ†∏Ïò¥
function getMemberIdFromToken(token) {
  if (!token) return null;
  try {
    //1. ÌÜ†ÌÅ∞ÏùÑ .ÏúºÎ°ú Î∂ÑÎ¶¨
    const base64Payload = token.split(".")[1];
    //2. base64payload -> base64Î°ú ÎîîÏΩîÎî©
    const base64 = base64Payload.replace(/-/g, "+").replace(/_/g, "/");
    //3. base64 ÎîîÏΩîÎî© (atob ÏÇ¨Ïö©)
    const jsonPayload = decodeURIComponent(
      atob(base64)
        .split("")
        .map((c) => `%${("00" + c.charCodeAt(0).toString(16)).slice(-2)}`)
        .join("")
    );
    //4. JSON ÌååÏã±
    const payload = JSON.parse(jsonPayload);
    //5. member_id Î∞òÌôò
    return payload.member_id || payload.sub || null;
  } catch (e) {
    return null;
  }
}

export default function BoardDetail() {
  const { category, board_id } = useParams(); // URL ÌååÎùºÎØ∏ÌÑ∞ÏóêÏÑú Í≤åÏãúÍ∏Ä ID Ï∂îÏ∂ú
  const [post, setPost] = useState(null);
  const [error, setError] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
    axios
      .get(`/board/boardDetail/${category}/${board_id}`, {
        headers: {
          Authorization: `Bearer ${localStorage.getItem("accessToken")}`,
        },
      })
      .then((response) => setPost(response.data))
      .catch((error) => setError(error));
  }, [category, board_id]);

  if (error) {
    return <div>Í≤åÏãúÍ∏ÄÏùÑ Î∂àÎü¨Ïò§ÏßÄ Î™ªÌñàÏäµÎãàÎã§. {error.message}</div>;
  }

  if (!post) {
    return <div>Î°úÎî© Ï§ë...</div>;
  }

  const token = localStorage.getItem("accessToken");
  const loginMemberId = getMemberIdFromToken(token); // JWT ÌÜ†ÌÅ∞ÏóêÏÑú Î°úÍ∑∏Ïù∏Ìïú ÌöåÏõêÏùò IDÎ•º Í∞ÄÏ†∏Ïò¥
  //const loginRole = localStorage.getItem("role"); // "ADMIN" ÎòêÎäî "USER"

  // ÏûëÏÑ±Ïûê(member_id) ÎòêÎäî Í¥ÄÎ¶¨Ïûê(ADMIN)Îßå Î≤ÑÌäº Î≥¥Ïù¥Í≤å
  const canEditOrDelete = String(post.member_id) === String(loginMemberId) || localStorage.getItem("role") === "ROLE_ADMIN";

  console.log("board_Id:", board_id);

  // ÏÇ≠Ï†ú Í∏∞Îä•
  const handleDelete = async () => {
    if (window.confirm("Ï†ïÎßê ÏÇ≠Ï†úÌïòÏãúÍ≤†ÏäµÎãàÍπå?")) {
      try {
        await axios.delete(`/board/delete/${post.board_id}`, {
          headers: {
            Authorization: `Bearer ${localStorage.getItem("accessToken")}`,
          },
        });
        alert("ÏÇ≠Ï†úÎêòÏóàÏäµÎãàÎã§.");
        navigate(`/boardList/${category}`);
      } catch (err) {
        alert("ÏÇ≠Ï†úÏóê Ïã§Ìå®ÌñàÏäµÎãàÎã§.");
      }
    }
  };

  // ÏàòÏ†ï Í∏∞Îä• (ÏàòÏ†ï ÌèºÏúºÎ°ú Ïù¥Îèô)
  const handleEdit = () => {
    navigate(`/boardEditForm/${category}/${post.board_id}`, {
      state: { category: post.category || "board" },
    });
  };

  console.log(
    "loginMemberId:",
    loginMemberId,
    "post.member_id:",
    post.member_id
  );

  // ÎåìÍ∏Ä Í∏∞Îä•ÏùÑ ÏúÑÌïú props ÏÑ§Ï†ï
  const data = [
    {
      userId: "02b",
      comId: "017",
      fullName: "Lily",
      userProfile: "https://www.linkedin.com/in/riya-negi-8879631a9/",
      text: "I think you have a pointü§î",
      avatarUrl: "https://ui-avatars.com/api/name=Lily&background=random",
      replies: [],
    },
  ];
  
  const currentUser = {
    currentUserId: loginMemberId,
    currentUserImg: "https://ui-avatars.com/api/name=Riya&background=random",
    currentUserProfile: "https://www.linkedin.com/in/riya-negi-8879631a9/",
    currentUserFullName: post.name,
  };

  return (
    <BoardDetailStyle>
      <div
        style={{
          width: "50%",
          height: "80%",
          margin: "40px auto",
          fontFamily: "GmarketSansMedium",
        }}
        className="boardDetailContainer"
      >
        <h2>{post.title}</h2>
        <div style={{ color: "#888", marginBottom: "10px" }}>
          ÏûëÏÑ±Ïûê: {post.name} | Ï°∞ÌöåÏàò: {post.views} | ÏûëÏÑ±Ïùº: {post.reg_date}
        </div>
        <div
          style={{ minHeight: "200px", fontSize: "18px", marginTop: "20px" }}
        >
          {post.content}
        </div>
        {canEditOrDelete && (
          <div style={{ marginTop: "30px", textAlign: "right" }}>
            <button onClick={handleEdit} style={{ marginRight: "10px" }}>
              ÏàòÏ†ï
            </button>
            <button onClick={handleDelete}>ÏÇ≠Ï†ú</button>
          </div>
        )}
        <br></br>
        <hr></hr>
        <div>
          <CommentSection
            currentUser={currentUser}
            commentData={data}
            onSubmitAction={data => console.log('check submit, ', data)}
            currentData={data => console.log('current data', data)}
          />
        </div>
      </div>
    </BoardDetailStyle>
  );
}
