const { default: styled } = require("styled-components");

const boardInsertFormContainer = styled.div`
  margin-top: 150px;
  height: 1000px;
  width: 100%;

  h2 {
    text-align: center;
  }

  table {
    margin-left: auto;
    margin-right: auto;
    border-collapse: collapse;
  }

  .popup {
    position: fixed;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    background: rgba(0, 0, 0, 0.5);
    display: flex;
    justify-content: center;
    align-items: center;
  }

  .popupContent {
    background: white;
    padding: 20px;
    border-radius: 8px;
    text-align: center;
    box-shadow: 0 4px 6px rgba(0, 0, 0, 0.1);
  }

  .popupContent h3 {
    margin-bottom: 10px;
  }

  .popupContent button {
    margin-top: 10px;
    padding: 10px 20px;
    background-color: #007bff;
    color: white;
    border: none;
    border-radius: 4px;
    cursor: pointer;
  }

  .popupContent button:hover {
    background-color: #0056b3;
  }
`;

export default boardInsertFormContainer;
