import PayComp from './PayStyle';
import { useLocation, useNavigate, useParams } from 'react-router-dom';
import { useEffect, useState } from 'react';
import MemberApi from '../../../api/MemberApi';
import GoodsApi from '../../../api/GoodsApi';

export default function Pay() {
  const location = useLocation();
  const navigate = useNavigate();
  const [goods, setGoods] = useState([]);
  const goodsList = location.state?.goods || [];
  const [payment, setPayment] = useState();
  const goodsImg = process.env.PUBLIC_URL + '/images/pic1.png';

  const [member, setMember] = useState([]);

  // 사용자(구매자) 정보
  const memdetail = async () => {
    MemberApi.detail()
      .then((response) => {
        setMember(response);
      })
      .catch((err) => {});
  };

  // 결제 로직 수행(BackEnd)
  const pay = async (goods, payment) => {
    const payload = {
      goodsList: goods,
      payment: payment,
    };
    GoodsApi.pay(payload) // 여기가 호출
      .then((response) => {
        alert('GoodsApi.pay() 성공');
        navigate('/user/orderdetail', { state: { orderList: response } });
      })
      .catch((err) => {
        alert('GoodsApi.pay() 에러');
      });
  };

  // 걸제수단 핸들링
  const handlePaymentChange = (e) => {
    setPayment(e.target.value);
  };

  useEffect(() => {
    console.log('goodsList:', goodsList);
    memdetail();
    setGoods(goodsList);
  }, []);

  return (
    <PayComp>
      <div className='container'>
        <section>
          <h2>구 매 자</h2>
          <hr />
          <div className='title'>구매자</div>
          <table className='payment'>
            <tbody>
              <tr>
                <th>이름</th>
                <td>{member.name}</td>
              </tr>
              <tr>
                <th>이메일</th>
                <td>{member.email}</td>
              </tr>
              <tr>
                <th>연락처</th>
                <td>{member.phone}</td>
              </tr>
            </tbody>
          </table>
        </section>
        <br />
        <section>
          <h2>배 송 지</h2>
          <hr />
          <div className='title'>
            수령인&nbsp;&nbsp; <button onClick={() => {}}>수정</button>
          </div>
          <table>
            <tbody>
              <tr>
                <th>이름</th>
                <td>{member.name}</td>
              </tr>
              <tr>
                <th>배송지</th>
                <td>경기 성남시</td>
              </tr>
              <tr>
                <th>연락처</th>
                <td>{member.phone}</td>
              </tr>
              <tr>
                <th>요청사항</th>
                <td>
                  문 앞(직접수령) &nbsp;&nbsp; <button>수정</button>
                </td>
              </tr>
            </tbody>
          </table>
        </section>
        <br />
        <section>
          <h2>구 매 목 록</h2>
          <hr />
          <div className='title'>상품정보</div>
          {goods.map((item, index) => (
            <div className='goodslist' key={index}>
              <div>{item.goods?.goods_name}</div>
              <div>{item.goods?.description}</div>
            </div>
          ))}
        </section>
        <br />
        <section>
          <h2>결 제</h2>
          <hr />
          <div className='title'>결제 정보</div>
          <table>
            <tbody>
              <tr>
                <th>상품 가격</th>
                <td>
                  {goods.map((item, index) => (
                    <div className='goodslist' key={index}>
                      <div>{item.goods?.price}</div>
                    </div>
                  ))}{' '}
                  원
                </td>
              </tr>
              <tr>
                <th>배송비</th>
                <td>3000 원</td>
              </tr>
              <tr>
                <th>최종결제금액</th>
                <td>15000 원</td>
              </tr>
              <tr>
                <th>결제방법</th>
                <td>
                  <label>
                    <input type='radio' name='payment' value='ACCOUNT' checked={payment === 'ACCOUNT'} onChange={handlePaymentChange} /> 계좌이체 <br />
                  </label>
                  <label>
                    <input type='radio' name='payment' value='POINT' checked={payment === 'POINT'} onChange={handlePaymentChange} /> 포인트결제 &nbsp; &nbsp; &nbsp;
                    <span className='badge'>최대 캐시적립</span> <br />
                  </label>
                  <label>
                    <input type='radio' name='payment' value='CARD' checked={payment === 'CARD'} onChange={handlePaymentChange} /> 신용/체크카드 <br />
                  </label>
                  <label>
                    <input type='radio' name='payment' value='CORPCARD' checked={payment === 'CORPCARD'} onChange={handlePaymentChange} /> 법인카드 <br />
                  </label>
                  <label>
                    <input type='radio' name='payment' value='PHONE' checked={payment === 'PHONE'} onChange={handlePaymentChange} /> 휴대폰 <br />
                  </label>
                  <label>
                    <input type='radio' name='payment' value='NOACOUNT' checked={payment === 'NOACOUNT'} onChange={handlePaymentChange} /> 무통장입금(상세조회) <br />
                  </label>
                </td>
              </tr>
              <tr>
                <th>연락처</th>
                <td>010-0000-0000</td>
              </tr>
              <tr>
                <th>요청사항</th>
                <td>문 앞(직접수령)</td>
              </tr>
            </tbody>
          </table>
        </section>
        <section>
          <button className='pay' onClick={() => pay(goods, payment)}>
            결제하기
          </button>
          &nbsp;&nbsp; <button className='cancel'>메인 페이지로</button>
        </section>
      </div>
    </PayComp>
  );
}
