import React from "react"; 

function CommentList({ comments, onDeleteComment,  }) {

  return (
    <ul>
      {comments.map((c) => (
        <li key={c.comment_id}>
          <strong>{c.member_id}</strong>: {c.content} ({c.reg_date})
          <button 
            style={{ marginLeft: "10px" }}
            onClick={() => onDeleteComment(c.comment_id)}  
          >삭제
          </button>
        </li>
      ))}
    </ul>
  );

}

export default CommentList;
