import React, { useState } from "react";
import BoardInsertFormStyle from "./BoardInsertFormStyle";

export default function BoardInsertForm() {
  const [isPopupVisible, setPopupVisible] = useState(false);
  const [formData, setFormData] = useState({ title: "", content: "" });

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    setPopupVisible(true); // 팝업 표시
  };

  const closePopup = () => {
    setPopupVisible(false); // 팝업 닫기
  };

  return (
    <BoardInsertFormStyle>
      <div className="boardInsertFormContainer">
        <h2>게시글 작성</h2>
        <form onSubmit={handleSubmit}>
          <table>
            <tr>
              <td><label htmlFor="title">제목</label></td>
            </tr>  
            <tr>
              <td>
                <input
                  type="text"
                  id="title"
                  name="title"
                  value={formData.title}
                  onChange={handleInputChange}
                />
              </td>
            </tr>
            <tr>
              <td>
                <label htmlFor="content">내용</label>
              </td>
            </tr>
            <tr>
              <td>
                <textarea
                  id="content"
                  name="content"
                  value={formData.content}
                  onChange={handleInputChange}
                ></textarea>
              </td>
            </tr>
            <tr>
              <td>
                <button type="submit">작성하기</button>
              </td>
            </tr>
          </table>
        </form>

        {isPopupVisible && (
          <div className="popup">
            <div className="popupContent">
              <h3>작성 내용 확인</h3>
              <p><strong>제목:</strong> {formData.title}</p>
              <p><strong>내용:</strong> {formData.content}</p>
              <button onClick={closePopup}>닫기</button>
            </div>
          </div>
        )}
      </div>
    </BoardInsertFormStyle>
  );
}
