import styled from 'styled-components';

const CartComp = styled.div`
  .container {
    width: 1000px;
    height: 1200px;
    padding: 0;
    margin-top: 150px;
    margin: auto;
    border: 1px solid rgb(230, 230, 230);
    display: flex;
    flex-direction: column;
    .top {
      margin-top: 0px;
      margin-left: 50px;
    }
    .body {
      width: 900px;
      height: 900px;
      margin: 0 auto;
      display: flex;
      flex-direction: row;
      border: 1px solid rgb(230, 230, 230);

      .left {
        width: 600px;
        height: 300px;
        margin-top: 50px;
        border: 1px solid rgb(230, 230, 230);
        display: flex;
        flex-direction: row;
        align-items: center;
        justify-content: center;

        .prodleft {
          width: 200px;
          height: 200px;
          margin-top: 0;
          overflow: hidden;
          display: flex;
          flex-direction: column;
          align-items: left;

          .cartimage {
            width: 100px;
            height: 80%;
            margin-left: 30px;
          }
          .seller {
            width: 0px;
          }
        }

        .prodright {
          width: 300px;
        }
      }
    }
  }

  .right {
    width: 300px;
    height: 500px;

    margin: 50px;
    display: flex;
    flex-direction: column;
  }
  .buy {
    width: 250px;
    height: 50px;
    margin: 20px auto;
  }
`;
export default CartComp;
