import axios from 'axios';

const KH_DOMAIN = 'http://localhost:8080';
export async function goodsApi(uri, method, requestData) {
  // 1.1) headers
  let headers = '';
  if (uri.indexOf('cart') >= 0) {
    headers = { 'Content-Type': 'multipart/form-data' };
  } else if (uri !== null) {
    headers = { 'Content-Type': 'application/json' };
  } else {
    headers = { 'Content-Type': 'application/json' };
  }

  // 1.2) axios 전송 options
  let options = {
    url: `${KH_DOMAIN}/${uri}`,
    method: method,
    headers: headers,
  };
  // => 전송할 Data 가 있으면 추가할수 있도록 "," 추가함

  // 1.3) 전송 Data(requestData) 있는 경우 data 속성 추가
  if (requestData) {
    options.data = requestData;
  }

  console.log(`** apiCall options.method=${options.method}`);
  console.log(`** apiCall options.url=${options.uri}`);
  console.log(`** apiCall options.data=${options.headers}`);

  // 1.3) Axios 요청
  return await axios(options)
    .then((response) => {
      return response.data;
    })
    .catch((err) => {
      console.error(`** apiCall Error status=${err.response.status}, message=${err.message}`);
      return Promise.reject(err.response.status);
    }); //catch
} //apiCall
