const { default: styled } = require("styled-components");

const CommentFormStyle = styled.div`
  .comment-form-container {
    width: 100%;

    .comment-form {
      
      input {
        width: 80%;
        height: 30px;
        padding: 10px;
        font-size: 16px;
        border: none;
        border-bottom: 2px solid #eee;
        border-radius: 5px;
        box-sizing: border-box;
      }

      
    }  
  }  
`;

export default CommentFormStyle;
