import { useEffect, useState } from "react";
import MyBoardListComp from "./MyBoardListStyle";
import PageNumber from "../../components/util/PageNumber";
import { useNavigate } from "react-router-dom";

export default function MyBoardList() {
  const navigate = useNavigate();
  const location = useLocation();

  const [listData, setListData] = useState([]);
  const [sort, setSort] = useState("desc");
  const params = new URLSearchParams(location.search);
  const searchType = params.get("searchType") || "title";
  const searchKeyword = params.get("searchKeyword") || "";
  const page = parseInt(params.get("page")) || 0;

  const token = localStorage.getItem("accessToken");
  function getMemberIdFromToken(token) {
    if (!token) return null;
    try {
      const base64Payload = token.split('.')[1];
      const base64 = base64Payload.replace(/-/g, '+').replace(/_/g, '/');
      const jsonPayload = decodeURIComponent(
        atob(base64)
          .split('')
          .map(c => `%${('00' + c.charCodeAt(0).toString(16)).slice(-2)}`)
          .join('')
      );
      const payload = JSON.parse(jsonPayload);
      return payload.member_id || payload.sub || null;
    } catch (e) {
      return null;
    }
  }
  const memberId = getMemberIdFromToken(token);

  //페이징 정보
  const [paging, setPaging] = useState({
    start: 0,
    end: 4,
    isPrev: false,
    isNext: true,
    totalElement: 0,
    totalPages: 0,
  });

 
  //검색 목록 Api
  const getPageList = async () => {
    const pages = {
      page: page,
      size: 12,
      sortBy: sort,
      keyword: keyword,
      type: type,
    };
    const result = await AdminApi.getPageList(pages);

    //컨텐츠 저장
    setUserList(result.content);
    let temp = Math.floor(page / 5) * 5;

    //페이지번호 정보 저장
    setPaging({
      start: temp,
      end: Math.min(temp + 5, result.totalPages),
      isPrev: result.prev,
      isNext: result.next,
      totalElement: result.totalElements,
      totalPages: result.totalPages,
    });
  };

  //검색 버튼 클릭
  const searchClick = () => {
    setPage(0);
    getPageList();
  };


  useEffect(() => {
    // 카테고리가 없으면 기본 board로 설정
    const apiUrl = categoryApiMap[category] || "/board/boardList/free";
    const params = { page, size: paging.size };
    if (searchKeyword.trim() !== "") {
      params.searchType = searchType;
      params.searchKeyword = searchKeyword;
    }
    axios
      .get(apiUrl, { params })
      .then((response) => {
        setListData(response.data.content || []);
        let temp = Math.floor(page / 3) * 3;
        setPaging(prev => ({ 
          ...prev,
          page: response.data.page,
          size: response.data.size,
          totalElements: response.data.totalElements,
          totalPages: response.data.totalPages,
          isPrev: response.data.prev,                 // javaBean 규약으로 인해 boolean type의 변수는 isPrev가 아닌 prev로 되어있음
          isNext: response.data.next,
          start: temp,
          end: Math.min(temp +3, response.data.totalPages),
        }));
      })
      .catch((error) => setError(error));
      // eslint-disable-next-line
  }, [category, location.search]);
  

  
  return (
    <MyBoardListComp>
      <div className="list_container">
        <h3>내 게시물 목록</h3>
        <hr />
        <div className="search">
          <div className="search_type">
            <select
              name="type"
              id="type"
              value={type}
              onChange={(e) => setType(e.target.value)}
            >
              <option value="all">전체</option>
              <option value="title">제목</option>
              <option value="content">내용</option>
            </select>
            <select
              name="sort"
              id="sort"
              value={sort}
              onChange={(e) => setSort(e.target.value)}
            >
              <option value="desc">최신순</option>
              <option value="asc">오래된 순</option>
            </select>
          </div>

          <div className="search_input">
            <input
              type="text"
              value={keyword}
              onChange={(e) => setKeyword(e.target.value)}
            />
            <button className="search_btn" onClick={() => searchClick()}>
              검색
            </button>
          </div>
        </div>
        <div className="list_view">
          <table>
            <tr>
              <th>번호</th>
              <th>게시판</th>
              <th style={{ width: "25%" }}>제목</th>
              <th style={{ width: "10%" }}>작성자</th>
              <th>작성일</th>
            </tr>

            {userList.length > 0 ? (
              userList.map((m, index) => (
                <tr>
                  <td align="center">{index + 1 + page * 12}</td>
                  <td id="category">{m.category}</td>
                  <td id="title">{m.title}</td>
                  <td id="name_col">{m.name}</td>
                  <td id="date_col">{m.reg_date}</td>
                </tr>
              ))
            ) : (
              <tr className="user_empty">
                <td colSpan="8">작성한 게시물이 없습니다.</td>
              </tr>
            )}
          </table>
        </div>
        <PageNumber page={page} setPage={setPage} paging={paging} />
      </div>
    </MyBoardListComp>
  );
}
