const { default: styled } = require("styled-components");


const boardDetailContainer = styled.div`
  margin-top: 200px;
  height: 1000px;
  width: 100%;
  .boardDetailContainer {
    width: 60%;
    margin: 40px auto;
    font-family: "GmarketSansMedium";

    h2 {
      font-size: 28px;
      margin-bottom: 20px;
    }

    .boardDetailInfo {
      color: #888;
      margin-bottom: 10px;
    }

    .boardDetailContent {
      min-height: 200px;
      font-size: 18px;
      margin-top: 20px;
    }

    .buttonGroup {
      display: flex;
      justify-content: flex-end;
      margin-top: 20px;

      button {
        padding: 10px 20px;
        margin-left: 10px;
        font-size: 16px;
        cursor: pointer;
        background-color: #f8e776;
        border: none;
        border-radius: 4px;
        transition: background-color 0.3s;

        &:hover {
          background-color: #e6d15c;
        }
      }
    }
  }
`;

export default boardDetailContainer;
