import BoardInsertFormStyle from "./BoardInsertFormStyle";
import React, { useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";

export default function BoardInsertForm() {
  const [title, setTitle] = useState("");
  const [content, setContent] = useState("");
  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.post("/board/insert", { title, content });
      alert("게시글이 등록되었습니다.");
      navigate("/boardList"); // 등록 후 게시판 목록으로 이동
    } catch (err) {
      alert("게시글 등록에 실패했습니다.");
    }
  };

  return (
    <BoardInsertFormStyle>
      <div className="boardInsertFormContainer">
        <h2>게시글 작성</h2>
        <form onSubmit={handleSubmit}>
          <table>
            <tbody>
              <tr>
                <td><label htmlFor="title">제목</label></td>
              </tr>
              <tr>
                <td>
                  <input
                    type="text"
                    id="title"
                    name="title"
                    value={title}
                    onChange={e => setTitle(e.target.value)}
                    required
                  />
                </td>
              </tr>
              <tr>
                <td><label htmlFor="content">내용</label></td>
              </tr>
              <tr>
                <td>
                  <textarea
                    id="content"
                    name="content"
                    value={content}
                    onChange={e => setContent(e.target.value)}
                    required
                  ></textarea>
                </td>
              </tr>
              <tr>
                <td>
                  <button type="submit">등록하기</button>
                </td>
              </tr>
            </tbody>
          </table>
        </form>
      </div>
    </BoardInsertFormStyle>
  );
}
