import axios from 'axios';

export async function goodsApi(uri, method, prodId) {
  const KH_DOMAIN = 'http://localhost:8080';
  // 1.1) headers
  let headers = '';

  // 1.2) axios 전송 options
  let options = {
    url: `${KH_DOMAIN}/${uri}`,
    method: method,
    headers: headers,
  };


  // 1.3) 전송 Data(requestData) 있는 경우 data 속성 추가
  console.log(`** apiCall options.method=${options.method}`);
  console.log(`** apiCall options.url=${options.uri}`);
  console.log(`** apiCall options.data=${options.headers}`);
  console.log(`** apiCall options.data=${options.data}`);

  // 1.3) Axios 요청
  return await axios(options)
    .then((response) => {
      return response.data;
    })
    .catch((err) => {
      console.error(`** apiCall Error status=${err.response.status}, message=${err.message}`);
      return Promise.reject(err.response.status);
    }); //catch
} //apiCall
