const { default: styled } = require("styled-components");

const CommentFormStyle = styled.div`
  .comment-form-container {
    width: 100%;

    comment-form {
      
      display: flex;
      justify-content: center;
      align-items: center;
      margin-top: 20px;
      padding: 10px;
      border-radius: 10px;
      background-color: #f8f8f8;    
    }  
  }  
`;

export default CommentFormStyle;
