    cursor: pointer;
    border-left: 1px solid #ccc;
    border-radius: 0 8px 8px 0;
    display: flex;
    align-items: center;
    justify-content: center;
    transition: background 0.2s;
  }
  button:hover {
    background: #f8e776;
  }
}
`;
export default boardListContainer;
