import React, { useEffect, useState } from "react";
import axios from "axios";
import BoardListStyle from "./BoardListStyle";
import { useNavigate, useParams, useLocation } from "react-router-dom";
import PageNumber from "../util/PageNumber";

export default function BoardList() {
  const { category } = useParams(); // URL ÌååÎùºÎØ∏ÌÑ∞ÏóêÏÑú Ïπ¥ÌÖåÍ≥†Î¶¨ Ï∂îÏ∂ú
  const [listData, setListData] = useState([]);
  const [error, setError] = useState(null);
  const navigate = useNavigate();
  const location = useLocation();
  const params = new URLSearchParams(location.search);
  const searchType = params.get("searchType") || "title";
  const searchKeyword = params.get("searchKeyword") || "";
  const page = parseInt(params.get("page")) || 0;
  //const [searchType, setSearchType] = useState("title");
  //const [searchKeyword, setSearchKeyword] = useState("");
  const [inputKeyword, setInputKeyword] = useState(searchKeyword); // searchKeywordÏùò Ï¥àÍ∏∞Í∞íÏùÑ inputKeywordÎ°ú ÏÑ§Ï†ï

  useEffect(() => {
    setInputKeyword(searchKeyword);
  }, [searchKeyword]);

  // pageNumber ÏÉÅÌÉú Î≥ÄÏàò Ï∂îÍ∞Ä
  const [paging, setPaging] = useState({
    page: 0,
    size: 3,
    totalElements: 0,
    totalPages: 1,
    isPrev: false,
    isNext: true,
    start: 0,
    end: 1,
  });
  //const [page, setPage] = useState(0);

  // Ïπ¥ÌÖåÍ≥†Î¶¨Î≥Ñ API ÏóîÎìúÌè¨Ïù∏Ìä∏ Îß§Ìïë
  const categoryApiMap = {
    notice: "/board/boardList/notice",
    community: "/board/boardList/community",
    faq: "/board/boardList/faq",
    free: "/board/boardList/free",
  };

  // Ïπ¥ÌÖåÍ≥†Î¶¨Î≥Ñ Í≤åÏãúÌåê Ïù¥Î¶Ñ Îß§Ìïë
  const categoryNameMap = {
    notice: "Í≥µÏßÄÏÇ¨Ìï≠",
    community: "Ïª§ÎÆ§ÎãàÌã∞",
    faq: "Î¨∏Ïùò/FAQ",
    free: "ÏûêÏú†Í≤åÏãúÌåê",
  };

  useEffect(() => {
    // Ïπ¥ÌÖåÍ≥†Î¶¨Í∞Ä ÏóÜÏúºÎ©¥ Í∏∞Î≥∏ boardÎ°ú ÏÑ§Ï†ï
    const apiUrl = categoryApiMap[category] || "/board/boardList/free";
    const params = { page, size: paging.size };
    if (searchKeyword.trim() !== "") {
      params.searchType = searchType;
      params.searchKeyword = searchKeyword;
    }
    axios
      .get(apiUrl, { params })
      .then((response) => {
        setListData(response.data.content || []);
        let temp = Math.floor(page / 3) * 3;
        setPaging((prev) => ({
          ...prev,
          page: response.data.page,
          size: response.data.size,
          totalElements: response.data.totalElements,
          totalPages: response.data.totalPages,
          isPrev: response.data.prev, // javaBean Í∑úÏïΩÏúºÎ°ú Ïù∏Ìï¥ boolean typeÏùò Î≥ÄÏàòÎäî isPrevÍ∞Ä ÏïÑÎãå prevÎ°ú ÎêòÏñ¥ÏûàÏùå
          isNext: response.data.next,
          start: temp,
          end: Math.min(temp + 3, response.data.totalPages),
        }));
      })
      .catch((error) => setError(error));
    // eslint-disable-next-line
  }, [category, location.search]);

  if (error) {
    // ÏÑúÎ≤Ñ ÏóêÎü¨ ÏΩîÎìúÏóê Îî∞Îùº Î©îÏãúÏßÄ Î∂ÑÍ∏∞
    if (error.response && error.response.status === 502) {
      return <div>{error.response.data}</div>;
    }
    return <div>Í≤åÏãúÌåêÏùÑ Î∂àÎü¨Ïò§ÏßÄ Î™ªÌñàÏäµÎãàÎã§. =&gt; {error.message}</div>;
  }

  //Í≤ÄÏÉâ Í∏∞Îä•

  const handleSearch = (e) => {
    if (e) e.preventDefault();
    const params = new URLSearchParams();
    params.set("searchType", searchType);
    params.set("searchKeyword", inputKeyword);
    params.set("page", 0);
    navigate({
      pathname: location.pathname,
      search: params.toString(),
    });
  };

  return (
    <BoardListStyle>
      <div className="boardListContainer">
        <div className="boardListMenuContainer">
          <ul className="boardListMenu">
            {/* ÌÅ¥Î¶≠ÌïòÎ©¥ Ìï¥Îãπ Ïπ¥ÌÖåÍ≥†Î¶¨Î°ú Ïù¥Îèô */}
            <li onClick={() => navigate("/boardList/notice")}>Í≥µÏßÄÏÇ¨Ìï≠</li>
            <li onClick={() => navigate("/boardList/community")}>Ïª§ÎÆ§ÎãàÌã∞</li>
            <li onClick={() => navigate("/boardList/faq")}>Q&A</li>
            <li onClick={() => navigate("/boardList/free")}>Í≤åÏãúÌåê</li>
          </ul>
        </div>
        <table>
          <thead>
            <tr>
              <td colSpan={5} height={50}>
                {/* Ìï¥Îãπ Í≤åÏãúÌåêÏùò Ï¢ÖÎ•òÏóê Îî∞Îùº Í≤åÏãúÌåê Ïù¥Î¶Ñ ÌëúÏãú */}
                {categoryNameMap[category] || "ÏûêÏú†Í≤åÏãúÌåê"}
              </td>
            </tr>
            <tr style={{ backgroundColor: " #f8e776" }}>
              <th>NO</th>
              <th>Ï†úÎ™©</th>
              <th>ÏûëÏÑ±Ïûê</th>
              <th>Ï°∞ÌöåÏàò</th>
              <th>ÏûëÏÑ±Ïùº</th>
            </tr>
          </thead>
          <tbody>
            {!listData || listData.length === 0 ? (
              <tr>
                <td
                  colSpan={5}
                  className="center"
                  style={{ textAlign: "center", padding: "20px" }}
                >
                  Í≤åÏãúÍ∏ÄÏù¥ ÏóÜÏäµÎãàÎã§.
                </td>
              </tr>
            ) : (
              listData.map((b, index) => (
                <tr key={index}>
                  <td className="center">
                    {paging.totalElements - paging.page * paging.size - index}
                  </td>
                  <td
                    className="center"
                    onClick={() =>
                      navigate(
                        `/boardDetail/${category}/${b.board_id}${location.search}`
                      )
                    }
                    style={{ cursor: "pointer", width: "50%" }}
                  >
                    {b.title}
                  </td>
                  <td className="center">{b.name}</td>
                  <td className="center">{b.views}</td>
                  <td className="center" style={{width:"20%"}}>{b.reg_date}</td>
                </tr>
              ))
            )}
            <tr>
              <td colSpan={5} align="right">
                <button
                  type="button"
                  onClick={() => {
                    if (localStorage.getItem("accessToken") != null) {
                      navigate(
                        `/boardInsertForm?category=${category || "board"}`
                      );
                    } else {
                      alert("Î°úÍ∑∏Ïù∏ Ìï¥Ï£ºÏÑ∏Ïöî");
                      navigate("/login?redirectTo=/boardInsertForm");
                    }
                  }}
                >
                  Í∏ÄÏì∞Í∏∞
                </button>
              </td>
            </tr>
          </tbody>
        </table>
        <div className="pageNumber">
          <PageNumber
            page={page}
            setPage={(newPage) => {
              const params = new URLSearchParams(location.search);
              params.set("page", newPage);
              navigate({
                pathname: location.pathname,
                search: params.toString(),
              });
